import React from 'react';
import logo from './logo.svg';
import { useState } from 'react';
import './App.css';

type Note = {
  id: number;
  title: string;
  content: string;
}

const App = () => {
  const [notes, setNotes] = useState<Note[]>([{
    id: 1,
    title: "a",
    content: "abc"
  },{
    id: 2,
    title: "b",
    content: "bcd"
  },{
    id: 3,
    title: "c",
    content: "bcd"
  },{
    id: 4,
    title: "d",
    content: "bcd"
  },{
    id: 5,
    title: "e",
    content: "bcd"
  }]);

  const [title, setTitle] = useState("");
  const [content, setContent] = useState("");
  const [selectedNote, setSelectedNote] = useState<Note|null>(null);

  const handleAddNote = (event: React.FormEvent) => {
    event.preventDefault();
    const newNote: Note = {
      id: notes.length + 1,
      title: title,
      content: content
    };
    setNotes([newNote,...notes]);
    setTitle("");
    setContent("");
  };

  const handleNoteClick = (note:Note) => {
    setSelectedNote(note);
    setTitle(note.title);
    setContent(note.content);
  }

  const handleUpdateNote = (event: React.FormEvent) => {
    event.preventDefault();
    if(!selectedNote){
      return;
    }

    const updatedNote:Note = {
      id: selectedNote.id,
      title: title,
      content: content
    }

    const updatedNotesList = notes.map((note)=> note.id===selectedNote.id ? updatedNote : note)

    setNotes(updatedNotesList)
    setTitle("")
    setContent("")
    setSelectedNote(null);
  };

  const handleCancel = () => {
    setTitle("")
    setContent("")
    setSelectedNote(null);
  };

  const deleteNote = (event: React.MouseEvent, noteId: number) => {
    event.stopPropagation();
    const updatedNotes = notes.filter((note) => note.id != noteId)
    setNotes(updatedNotes);
  };

  return(
    <div className='app-container'>
      <form className='note-form' onSubmit={(event) => selectedNote ? handleUpdateNote(event) : handleAddNote(event)}>
        <input value={title} onChange={(event)=> setTitle(event.target.value)} placeholder='title' required></input> 
        <textarea value={content} onChange={(event)=> setContent(event.target.value)} placeholder='content' rows={10} required> </textarea>

        {selectedNote ? 
          (<div className='edit-buttons'>
            <button type='submit'>edit</button>
            <button onClick={handleCancel}>cancel</button>
          </div>) : (<button type='submit'>add note</button>)
        } 

      </form>
      <div className='note-grid'>
        {notes.map((note)=>(
          <div className='note-item' onClick={()=> handleNoteClick(note)}>
          <div className='note-header'>
            <button onClick={(event) => deleteNote(event, note.id)}>x</button>
          </div>
            <h2>{note.title}</h2>
            <p>{note.content}</p>
          </div>
        ))}
      </div>
    </div>
  );
}


export default App;
